    getCategoryItem: state => state.categories,
    getAddToCarts: state => state.addedToCart,
    brandsCount(state) {
        const counts = {};
        state.items.forEach(item => {
            counts[item.brand] = counts[item.brand] + 1 || 1;
        //    console.log(counts)
        });

