import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { ClientSideRowModelModule } from "@ag-grid-community/client-side-row-model";
import { RichSelectModule } from '@ag-grid-enterprise/rich-select';
import { MenuModule } from '@ag-grid-enterprise/menu';
import { ColumnsToolPanelModule } from '@ag-grid-enterprise/column-tool-panel';
import { SetFilterModule } from '@ag-grid-enterprise/set-filter';
import { AllCommunityModules } from '@ag-grid-community/all-modules';
import { MasterDetailModule } from '@ag-grid-enterprise/master-detail';

export default {
  metaInfo: {
    title: "Dashboard"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      columnDefsMod: null,
      rowData: null,
      rowDataMod: null,
      rowSelection: null,
      defaultColDef: null,
      frameworkComponents: null,
      detailCellRendererParams: null,
      domLayout: null,
      modules: [
        ClientSideRowModelModule,
        SetFilterModule,
        RichSelectModule,
        MenuModule,
        ColumnsToolPanelModule,
        MasterDetailModule,
      ],
      suppliers: [],
      orders: [],
      inspections: [],
      show: false,
      visible: false,
      ONumber: '',
      DatOrder: '',
      DatDelivery: '',
      OType: '',
      CNumber: '',
      DelStatus: '',
      Supplier: '',
      date: null,
      next: null,
      error: false,
      optionsSupplier: [],
      optionsType: [
        { value: 'Sample', text: 'Sample' },
        { value: 'Customer', text: 'Customer' },
      ],
      optionsStatus: [
        { value: 'Delivered', text: 'Delivered' },
        { value: 'Not Delivered', text: 'Not Delivered' },
      ],
      rows: [],
      columns: [
        {label: "Inspection Number", field: "txtInspectionNumber",},
        {label: "Inspection Status", field: "txtInspectionStatus",},
        {label: "Inspection Type", field: "txtInspectionType",},
        {label: "Inspection Date", field: "datInspection",},
        {label: "Inspector", field: "txtInspector",},
        {label: "Approval", field: "txtApproval",},
        {label: "Comment 1", field: "txtComment1",},
        {label: "Comment 2", field: "txtComment2",},
      ],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Order Number", field: "txtOrderNumber", filter: 'agTextColumnFilter', checkboxSelection: true,},
      {headerName: "Order Date", field: "datOrder", filter: 'agTextColumnFilter',},
      {headerName: "Delivery Date", field: "datDelivery", filter: 'agTextColumnFilter',},
      {headerName: "Order Type", field: "txtOrderType", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['Sample', 'Customer']}},
      {headerName: "Charge Number", field: "txtChargeNumber", filter: 'agTextColumnFilter',},
      {headerName: "Delivery Status", field: "txtDeliveryStatus", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['Delivered', 'Not Delivered']}},
      {headerName: "Supplier", field: "idSupplier.txtSupplierName", filter: 'agTextColumnFilter', editable: false},
    ],
    this.defaultColDef = {
      // flex: 1,
      editable: true,
      resizable: true,
      sortable: true,
      filter: true,
      floatingFilter: true,
    };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    this.gridApi = this.gridOptions.api;
    this.gridColumnApi = this.gridOptions.columnApi;
  },
  methods: {
    async getUserInfo() {
      // add the username of the logged in user to localStorage
      let endpoint = `/api/user/`;
      let data = await apiService(endpoint);
      let requestUser = data["username"];
      let requestSuperuser = data["is_superuser"]
      window.localStorage.setItem("username", requestUser);
      window.localStorage.setItem("superuser", requestSuperuser);
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    async cellValueChanged(event) {     
      let endpoint = `/api/order/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          txtOrderNumber: event.node.data.txtOrderNumber,
          datOrder: event.node.data.datOrder,
          datDelivery: event.node.data.datDelivery,
          txtOrderType: event.node.data.txtOrderType,
          txtChargeNumber: event.node.data.txtChargeNumber,
          txtDeliveryStatus: event.node.data.txtDeliveryStatus,
          // idSupplier: event.node.data.idSupplier,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    onGridReady(params) { 
      this.gridOptions.api.setQuickFilter("Not Delivered")

    },
    getOrder() {
      if (!this.error) {
        let endpoint = `/api/order-list/`;
        apiService(endpoint)
          .then(data => {
            this.orders.push(...data.results);
            // this.rowData = this.orders.filter(el => el.txtDeliveryStatus == 'Not Delivered')
            this.rowData = this.orders
          })
      }
    },
    getSup() {
      if (!this.error) {
        let endpoint = `/api/supplier/`;
        apiService(endpoint)
          .then(data => {
            this.suppliers.push(...data.results);
            this.optionsSupplier = this.suppliers
          })
      }
    },
    getInspections() {
      if (!this.error) {
        let endpoint = `/api/inspection/`;
        apiService(endpoint)
          .then(data => {
            this.inspections.push(...data.results);
            // this.rows = this.inspections
            this.rows = this.inspections.filter(el => el.txtInspectionStatus == 'in Progress' || el.txtInspectionStatus == 'on Hold')
          })
      }
    },
  },
  created() {
    this.getSup();
    this.getOrder();
    this.getInspections();
    this.getUserInfo();
  },
};
