import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { ClientSideRowModelModule } from "@ag-grid-community/client-side-row-model";
import { RichSelectModule } from '@ag-grid-enterprise/rich-select';
import { MenuModule } from '@ag-grid-enterprise/menu';
import { ColumnsToolPanelModule } from '@ag-grid-enterprise/column-tool-panel';
import { SetFilterModule } from '@ag-grid-enterprise/set-filter';
import { AllCommunityModules } from '@ag-grid-community/all-modules';
import { MasterDetailModule } from '@ag-grid-enterprise/master-detail';

export default {
  metaInfo: {    
    title: "Model"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      columnDefsMod: null,
      rowData: null,
      rowDataMod: null,
      rowSelection: null,
      defaultColDef: null,
      frameworkComponents: null,
      detailCellRendererParams: null,
      domLayout: null,
      modules: [
        ClientSideRowModelModule,
        SetFilterModule,
        RichSelectModule,
        MenuModule,
        ColumnsToolPanelModule,
        MasterDetailModule,
      ],
      suppliers: [],
      manufacturers: [],
      models: [],
      show: false,
      visible: false,
      MName: '',
      Manufacturer: '',
      Supplier: '',
      date: null,
      next: null,
      error: false,
      optionsSupplier: [],
      optionsManufacturer: [],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Model Name", field: "txtModelName", filter: 'agTextColumnFilter', checkboxSelection: true,},
      {headerName: "Manufacturer", field: "idManufacturer.txtManufacturerName", filter: 'agTextColumnFilter', editable: false},
      {headerName: "Supplier", field: "idSupplier.txtSupplierName", filter: 'agTextColumnFilter', editable: false},
    ],
    this.defaultColDef = {
      // flex: 1,
      editable: true,
      resizable: true,
      sortable: true,
      filter: true,
      floatingFilter: true,
    };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    this.gridApi = this.gridOptions.api;
    this.gridColumnApi = this.gridOptions.columnApi;
  },
  methods: {
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    async cellValueChanged(event) {     
      let endpoint = `/api/model/${event.node.data.id}/`;
      console.log(event.node.data.id)
      try {
        await apiService(endpoint, "PATCH", { 
          txtModelName: event.node.data.txtModelName,
          // idManufacturer: event.node.data.idManufacturer,
          // idSupplier: event.node.data.idSupplier,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    onGridReady(params) { 
      // this.autoSizeAll(false);
    },
    getManufacturer() {
      if (!this.error) {
        let endpoint = `/api/manufacturer/`;
        apiService(endpoint)
          .then(data => {
            this.manufacturers.push(...data.results);
            this.optionsManufacturer = this.manufacturers
          })
      }
    },
    getSupplier() {
      if (!this.error) {
        let endpoint = `/api/supplier/`;
        apiService(endpoint)
          .then(data => {
            this.suppliers.push(...data.results);
            this.optionsSupplier = this.suppliers
          })
      }
    },
    getModels() {
      if (!this.error) {
        let endpoint = `/api/model-list/`;
        apiService(endpoint)
          .then(data => {
            this.models.push(...data.results);
            this.rowData = this.models
          })
      }
    },
    addModel() {
      if (this.MName) {
        let endpoint = `/api/model/`;
        apiService(endpoint, "POST", { 
          txtModelName: this.MName,
          idManufacturer: this.Manufacturer,
          idSupplier: this.Supplier,
          })
          .then(data => {
            if (data) {
              console.log(data)
              this.models.unshift(data)
            } else {
              this.error = true;
            }
          })
        this.emptyInput();
        if (this.error) {
          this.error = false;
        }
        } else {
          this.error = true;
        }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Model?", {
          title: "Please Confirm",
          size: "sm",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => {
          if (value) {
            this.deleteModel();
          }
        })
    },
    async deleteModel() {
      var selectedRows = this.gridApi.getSelectedRows();
      let endpoint = `/api/model/${selectedRows[0].id}/`;
      try {
        var index = this.models.map(function(e) { return e.id; }).indexOf(selectedRows[0].id);
        this.$delete(this.models, index)
        await apiService(endpoint, "DELETE")
        this.show = this.show ? false : true
      }
      catch (err) {
        console.log(err)
      }
    },
    emptyInput() {
      this.MName = '',
      this.Manufacturer = [],
      this.Supplier = [],

      this.$root.$emit('bv::toggle::collapse', 'collapse-e')
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
  },
  created() {
    this.getManufacturer();
    this.getSupplier();
    this.getModels();
    this.getDateToday();
  },
};
