import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Item Orders"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      rowData: null,
      rowSelection: null,
      defaultColDef: null,
      excelStyles: null,
      frameworkComponents: null,
      domLayout: null,
      modules: AllModules,
      // VUE VARIABLES
      orders: [],
      show: false,
      visible: false,
      ONumber: '',
      DatOrder: '',
      DatDelivery: '',
      OType: '',
      CNumber: '',
      DelStatus: '',
      Supplier: '',
      date: null,
      next: null,
      error: false,
      optionsSupplier: [],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Item", field: "idItem.txtDescription", filter: 'agTextColumnFilter', checkboxSelection: true, editable: false},
      {headerName: "Amount", field: "numOrderAmount", filter: 'agTextColumnFilter',},
      {headerName: "Order Date", field: "datOrder", filter: 'agTextColumnFilter',},
      {headerName: "Delivery Date", field: "datDelivery", filter: 'agTextColumnFilter',},
      {headerName: "Order Number", field: "idOrder.txtOrderNumber", filter: 'agTextColumnFilter', editable: false},
    ],
    this.defaultColDef = { editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    this.gridApi = this.gridOptions.api;
    this.gridColumnApi = this.gridOptions.columnApi;

    if (window.OrderItemColState)
       this.gridOptions.columnApi.setColumnState(window.OrderItemColState);
    else
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
      this.gridApi.sizeColumnsToFit();
  },
  methods: {
    saveState() {
      window.OrderItemColState = this.gridOptions.columnApi.getColumnState();
    },
    restoreState() {
      window.OrderItemColState = null;
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    async cellValueChanged(event) {     
      let endpoint = `/api/order-item/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          datOrder: event.node.data.datOrder,
          datDelivery: event.node.data.datDelivery,
          numOrderAmount: event.node.data.numOrderAmount,
          // idItem: event.node.data.idItem,
          // idOrder: event.node.data.idOrder,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    onGridReady(params) { 
    },
    getItemOrder() {
      if (!this.error) {
        let endpoint = `/api/order-item-list/`;
        apiService(endpoint)
          .then(data => {
            this.orders.push(...data.results);
            this.rowData = this.orders
          })
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Order?", {
          title: "Please Confirm",
          size: "sm",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => {
          if (value) {
            this.deleteOrder();
          }
        })
    },
    async deleteOrder() {
      var selectedRows = this.gridApi.getSelectedRows();
      let endpoint = `/api/order/${selectedRows[0].id}/`;
      try {
        var index = this.orders.map(function(e) { return e.id; }).indexOf(selectedRows[0].id);
        this.$delete(this.orders, index)
        await apiService(endpoint, "DELETE")
        this.show = this.show ? false : true
      }
      catch (err) {
        console.log(err)
      }
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
  },
  created() {
    this.getItemOrder();
    this.getDateToday();
  },
};
