import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Device Attributes"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      rowData: null,
      rowSelection: null,
      defaultColDef: null,
      frameworkComponents: null,
      domLayout: null,
      modules: AllModules,

      // VUE VARIABLES
      attributes: [],
      idDevice: null,
      KeyAtt: null,
      NameAtt: null,
      ValueDef: null,
      ValueType: null,
      MeasureUnit: null,
      date: null,
      error: false,
      optionTypes: [
        { value: 'INTEGER', text: 'INTEGER' },
        { value: 'FLOAT', text: 'FLOAT' },
        { value: 'STRING', text: 'STRING' },
        { value: 'BOOLEAN', text: 'BOOLEAN' },
      ],
      optionUnits: [
        { value: 'µm', text: 'Mymetre' },
        { value: 'mm', text: 'Millimetre' },
        { value: 'cm', text: 'Centimetre' },
        { value: 'mg', text: 'Milligram' },
        { value: 'g', text: 'Gram' },
        { value: 'gb', text: 'Gigabyte' },
        { value: 'tb', text: 'Terabyte' },
      ],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Key Attribute", field: "txtKeyAttribute", filter: 'agTextColumnFilter',},
      {headerName: "Name Attribute", field: "txtNameAttribute", filter: 'agTextColumnFilter', },
      {headerName: "Value Default", field: "txtValueDefault", filter: 'agTextColumnFilter', },
      {headerName: "Value Type", field: "txtValueType", filter: 'agTextColumnFilter', 
        cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['INTEGER', 'FLOAT', 'STRING', 'BOOLEAN']}},
      {headerName: "Measure Unit", field: "txtMeasureUnit", filter: 'agTextColumnFilter', 
        cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['µm', 'mm', 'cm', 'mg', 'g', 'gb', 'tb']}},
    ],
    this.defaultColDef = { flex: 1, editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    if (window.DeviceAttributeColState)
       this.gridOptions.columnApi.setColumnState(window.DeviceAttributeColState);
    else
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
  },
  computed: {
    disabled: function () {
      return !this.KeyAtt ? true : !this.NameAtt ? true : !this.ValueDef ? true : !this.ValueType ? true : false; 
    },
  },
  methods: {
    saveState() {
      window.DeviceAttributeColState = this.gridOptions.columnApi.getColumnState();
    },
    restoreState() {
      window.DeviceAttributeColState = null;
    },
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    async updateAttributeGrid(event) {     
      let endpoint = `/api/device-template-attribute/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          txtKeyAttribute: event.node.data.txtKeyAttribute,
          txtNameAttribute: event.node.data.txtNameAttribute,
          txtValueDefault: event.node.data.txtValueDefault,
          txtValueType: event.node.data.txtValueType,
          txtMeasureUnit: event.node.data.txtMeasureUnit,
          idDevice: event.node.data.idDevice,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    getAtt() {
      if (!this.error) {
        this.attributes = [];
        let endpoint = `/api/device-template-attribute/`;
        apiService(endpoint)
          .then(data => {
            this.attributes.push(...data.results);
            this.rowData = this.attributes
          })
      }
    },
    addAtt() {
      let endpoint = `/api/device-template-attribute/`;
      apiService(endpoint, "POST", { 
        txtKeyAttribute: this.KeyAtt,
        txtNameAttribute: this.NameAtt,
        txtValueDefault: this.ValueDef,
        txtValueType: this.ValueType,
        txtMeasureUnit: this.MeasureUnit,
        })
        .then(response => {
          if (response != 'ERROR'){
            this.getAtt();
            this.emptyInput();
          } else {
            this.error = true;
          }
        })
      if (this.error) {
        this.error = false;
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Attribute? => " + params.node.data.txtNameAttribute, {
          title: "Please Confirm",
          size: "m",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => {
          if (value) {
            this.deleteAttribute(params);
          }
        })
    },
    async deleteAttribute(params) {
      let endpoint = `/api/device-template-attribute/${params.node.data.id}/`;
      try {
        await apiService(endpoint, "DELETE")
        this.getAtt();
      }
      catch (err) {
        console.log(err)
      }
    },
    emptyInput() {
      this.idDevice = [],
      this.KeyAtt = '',
      this.NameAtt = '',
      this.ValueType = [],
      this.ValueDef = '',
      this.MeasureUnit = [],
      this.$root.$emit('bv::toggle::collapse', 'collapse-e')
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    getContextMenu(params) {
      var result = [
        {
          name: 'Delete Attribute',
          action: () =>  this.openModal(params)
        },
        'export'
      ];
      return result;
    },
  },
  created() {
    this.getAtt();
    this.getDateToday();
  },
};
