import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Inspection"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      columnDefsMod: null,
      rowData: null,
      rowDataMod: null,
      rowSelection: null,
      defaultColDef: null,
      excelStyles: null,
      frameworkComponents: null,
      detailCellRendererParams: null,
      domLayout: null,
      modules: AllModules,
      inspections: [],
      show: false,
      visible: false,
      date: null,
      next: null,
      error: false,
      optionsInspectionStatus: [
        { value: 'in Progress', text: 'in Progress' },
        { value: 'on Hold', text: 'on Hold' },
        { value: 'Done', text: 'Done' },
      ]
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Inspection Number", field: "txtInspectionNumber", filter: 'agTextColumnFilter', checkboxSelection: true,},
      {headerName: "Inspection Status", field: "txtInspectionStatus", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['in Progress', 'on Hold', 'Done']}},
      {headerName: "Inspection Type", field: "txtInspectionType", filter: 'agTextColumnFilter',},
      {headerName: "Inspection Date", field: "datInspection", filter: 'agTextColumnFilter',},
      {headerName: "Inspector", field: "txtInspector", filter: 'agTextColumnFilter',},
      {headerName: "Approval", field: "txtApproval", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['Not Released', 'Released']}},
      {headerName: "Comment 1", field: "txtComment1", filter: 'agTextColumnFilter',},
      {headerName: "Comment 2", field: "txtComment2", filter: 'agTextColumnFilter',},
    ],
    this.defaultColDef = {
      // flex: 1,
      editable: true,
      resizable: true,
      sortable: true,
      filter: true,
      floatingFilter: true,
    };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    this.gridApi = this.gridOptions.api;
    this.gridColumnApi = this.gridOptions.columnApi;

    if (window.InspectionColState)
       this.gridOptions.columnApi.setColumnState(window.InspectionColState);
    else
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
  },
  methods: {
    saveState() {
      window.InspectionColState = this.gridOptions.columnApi.getColumnState();
    },
    restoreState() {
      window.InspectionColState = null;
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    async cellValueChanged(event) {     
      let endpoint = `/api/inspection/${event.node.data.id}/`;
      console.log(event.node.data.id)
      try {
        await apiService(endpoint, "PATCH", { 
          txtInspectionNumber: event.node.data.txtInspectionNumber,
          txtInspectionStatus: event.node.data.txtInspectionStatus,
          txtInspectionType: event.node.data.txtInspectionType,
          datInspection: event.node.data.datInspection,
          txtInspector: event.node.data.txtInspector,
          txtApproval: event.node.data.txtApproval,
          txtComment1: event.node.data.txtComment1,
          txtComment2: event.node.data.txtComment2,
          idOrderDevice: event.node.data.idOrderDevice,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    onGridReady(params) { 
    },
    getInspections() {
      if (!this.error) {
        let endpoint = `/api/inspection/`;
        apiService(endpoint)
          .then(data => {
            this.inspections.push(...data.results);
            this.rowData = this.inspections
          })
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Inspection?", {
          title: "Please Confirm",
          size: "sm",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => {
          if (value) {
            this.deleteInspection();
          }
        })
    },
    async deleteInspection() {
      var selectedRows = this.gridApi.getSelectedRows();
      let endpoint = `/api/inspection/${selectedRows[0].id}/`;
      try {
        var index = this.inspections.map(function(e) { return e.id; }).indexOf(selectedRows[0].id);
        this.$delete(this.inspections, index)
        await apiService(endpoint, "DELETE")
        this.show = this.show ? false : true
      }
      catch (err) {
        console.log(err)
      }
    },
    editInspection() {
      this.$route.push('inspection')
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
  },
  created() {
    this.getInspections();
    this.getDateToday();
  },
};
