import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Manufacturer"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      rowData: null,
      rowSelection: null,
      defaultColDef: null,
      excelStyles: null,
      frameworkComponents: null,
      domLayout: null,
      modules: AllModules,

      // VUE VARIABLES
      manufacturers: [],
      show: false,
      ManName: '',
      date: null,
      error: false,
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [ {headerName: "Manufacturer", field: "txtManufacturerName", filter: 'agTextColumnFilter', }, ],
    this.defaultColDef = { flex: 1, editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    this.gridApi = this.gridOptions.api;
    this.gridColumnApi = this.gridOptions.columnApi;
  },
  methods: {
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    addMan() {
      if (this.ManName) {
        let endpoint = `/api/manufacturer/`;
        apiService(endpoint, "POST", { txtManufacturerName: this.ManName, })
          .then(response => {
            if (response != 'ERROR'){
              this.getMan();
              this.emptyInput()
            } else {
              this.error = true;
            }
          })
        if (this.error) {
          this.error = false;
        }
      }
    },
    async updateManufacturerGrid(event) {     
      let endpoint = `/api/manufacturer/${event.node.data.id}/`;
      console.log(event.node.data.id)
      try {
        await apiService(endpoint, "PATCH", { 
          txtManufacturerName: event.node.data.txtManufacturerName,
        })
      }
      catch (err) {
        console.log(err)
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Manufacturer? => " + params.node.data.txtManufacturerName, {
          title: "Please Confirm",
          size: "m",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => {
          if (value) {
            this.deleteMan(params);
          }
        })
    },
    async deleteMan(params) {
      let endpoint = `/api/manufacturer/${params.node.data.id}/`;
      try {
        await apiService(endpoint, "DELETE")
        this.getMan();
      }
      catch (err) {
        console.log(err)
      }
    },
    emptyInput() {
      this.ManName = '',
      this.$root.$emit('bv::toggle::collapse', 'collapse-e')
    },
    getMan() {
      if (!this.error) {
        this.manufacturers = [];
        let endpoint = `/api/manufacturer/`;
        apiService(endpoint)
          .then(data => {
            this.manufacturers.push(...data.results);
            this.rowData = this.manufacturers
          })
      }
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    getContextMenu(params) {
      var result = [
        {
          name: 'Delete Manufacturer',
          action: () => this.openModal(params)
        },
        'separator',
        'export'
      ];
      return result;
    },
  },
  created() {
    this.getMan();
    this.getDateToday();
  },
};
