import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Device Orders"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      rowData: null,
      rowSelection: null,
      defaultColDef: null,
      excelStyles: null,
      frameworkComponents: null,
      domLayout: null,
      modules: AllModules,

      // VUE VARIABLES
      orders: [],
      show: false,
      visible: false,
      ONumber: '',
      DatOrder: '',
      DatDelivery: '',
      OType: '',
      date: null,
      next: null,
      error: false,
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Device", field: "idDevice.idModel.txtModelName", filter: 'agTextColumnFilter', checkboxSelection: true, editable: false,},
      {headerName: "Serial Number", field: "idDevice.txtSerialNumber", filter: 'agTextColumnFilter', editable: false,},
      {headerName: "Amount", field: "numOrderAmount", filter: 'agTextColumnFilter',},
      {headerName: "Order Date", field: "datOrder", filter: 'agTextColumnFilter',},
      {headerName: "Delivery Date", field: "datDelivery", filter: 'agTextColumnFilter',},
      {headerName: "Delivery Status", field: "txtDeliveryStatus", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['Delivered', 'Not Delivered']}},
      {headerName: "Order Number", field: "idOrder.txtOrderNumber", filter: 'agTextColumnFilter', editable: false,},
    ],
    this.defaultColDef = { flex: 1, editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    if (JSON.parse(localStorage.getItem('orderDevice_columnStats')) != null) {
       this.gridOptions.columnApi.setColumnState(JSON.parse(localStorage.getItem('orderDevice_columnStats')));
    } else {
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
    }
  },
  methods: {
    saveState() {
      localStorage.setItem('orderDevice_columnStats', JSON.stringify(this.gridOptions.columnApi.getColumnState()));
    },
    restoreState() {
      localStorage.setItem('orderDevice_columnStats', null);
    },
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    onGridReady(params) { 
    },
    async updateOrderDevice(event) {     
      let endpoint = `/api/order-device/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          datOrder: event.node.data.datOrder,
          datDelivery: event.node.data.datDelivery,
          numOrderAmount: event.node.data.numOrderAmount,
          txtDeliveryStatus: event.node.data.txtDeliveryStatus,
          // idDevice: event.node.data.idDevice,
          // idOrder: event.node.data.idOrder,
        })
        .catch(err => console.log(err));
      }
      catch (err) {
        console.log(err)
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Order? => " + params.node.data.idDevice.txtSerialNumber, {
          title: "Please Confirm",
          size: "m",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => { if (value) { this.deleteOrder(); } })
        .catch(err => console.log(err));
    },
    async deleteOrder(params) {
      let endpoint = `/api/order/${params.node.data.id}/`;
      try {
        await apiService(endpoint, "DELETE");
        this.getDeviceOrder();
      }
      catch (err) {
        console.log(err);
      }
    },
    getDeviceOrder() {
      if (!this.error) {
        let endpoint = `/api/order-device-list/`;
        apiService(endpoint)
          .then(data => {
            this.orders.push(...data.results);
            this.rowData = this.orders
          })
          .catch(err => console.log(err));
      }
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    getContextMenu(params) {
      var result = [
        {
          name: 'Delete Device Order',
          action: () =>  this.openModal(params)
        },
        'export'
      ];
      return result;
    },
  },
  created() {
    this.getDeviceOrder();
    this.getDateToday();
  },
};
