import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Template"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      columnDefsAtt: null,
      rowData: null,
      rowDataMod: null,
      rowSelection: null,
      defaultColDef: null,
      frameworkComponents: null,
      detailCellRendererParams: null,
      domLayout: null,
      excelStyles: null,
      modules: AllModules,

      // VUE VARIABLES
      templates: [],
      attributes: [],
      typeAttributes: [],
      selectedAttributes: [],
      selAtt1: [],
      selAtt2: [],
      selAtt3: [],
      selAtt4: [],
      show: false,
      visible: false,
      TempName: '',
      Type: '',
      IType: null,
      date: null,
      error: false,
      optionsItemTypes: [
        { value: 'Glass', text: 'Glass' },
        { value: 'Case', text: 'Case' },
        { value: 'Frame Standard', text: 'Frame Standard' },
        { value: 'Frame Eco', text: 'Frame Eco' },
      ],
      optionsType: [
        { value: 'MF', text: 'Mess & Funktionsprüfung' },
        { value: 'CH', text: 'Chemische Prüfung' },
        { value: 'PH', text: 'Physikalische Prüfung' },
        { value: 'CA', text: 'Case Prüfung' },
        { value: 'FR', text: 'Frame Prüfung' },
      ],
      optionsAttributes: [],
      optionAttsGlass: [],
      optionAttsCase: [],
      optionAttsFrameStandard: [],
      optionAttsFrameEco: [],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.columnDefs = [
      {headerName: "Template Name", field: "txtInspectionTemplateName", filter: 'agTextColumnFilter', cellRenderer: 'agGroupCellRenderer', checkboxSelection: true,},
      {headerName: "Template Type", field: "txtInspectionTemplateType", filter: 'agTextColumnFilter', cellEditor: 'agRichSelectCellEditor', 
        cellEditorParams: { cellHeight: 50, values: ['MF', 'CH', 'PH', 'CA', 'FR']}, },
    ],
    this.defaultColDef = { flex: 1, editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.detailCellRendererParams = {
      detailGridOptions: {
        rowSelection: 'single',
        suppressRowClickSelection: true,
        enableRangeSelection: true,
        pagination: true,
        paginationAutoPageSize: 20,
        columnDefs: [
          {headerName: "Item Type", field: "txtItemType",},
          {headerName: "Attribute Key", field: "txtKeyAttribute",},
          {headerName: "Attribute Name", field: "txtNameAttribute",},
          {headerName: "Default Value", field: "txtValueDefault",},
          {headerName: "Value Type", field: "txtValueType",},
          {headerName: "Parameter", field: "txtParameter",},
        ],
        defaultColDef: { flex: 1, editable: true, resizable: true, },
      },
      getDetailRowData: params => {
        params.successCallback(params.data.idInspectionTemplateAttribute);
      },
    }
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
  },
  mounted() {
    if (window.InspectionTemplateColState)
       this.gridOptions.columnApi.setColumnState(window.InspectionTemplateColState);
    else
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
  },
  computed: {
    disabled: function () {
      return !this.TempName ? true : !this.Type ? true : !this.IType ? true : false; 
    },
  },
  methods: {
    onBtExport() {
      this.gridApi.exportDataAsExcel({});
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    saveState() {
      window.InspectionTemplateColState = this.gridOptions.columnApi.getColumnState();
    },
    restoreState() {
      window.InspectionTemplateColState = null;
    },
    createInspection() {
      var selectedRows = this.gridApi.getSelectedRows();
      localStorage.setItem('ITempID', selectedRows[0].id)
      localStorage.setItem('ITempType', selectedRows[0].txtInspectionTemplateType)
      this.$router.push('inspection')
    },
    onChange(event) {
      this.typeAttributes = [];
      this.optionAttsGlass = [];
      this.optionAttsCase = [];
      this.optionAttsFrame = [];
      let endpoint = `/api/inspection-template-attribute/`;
      apiService(endpoint)
      .then(data => {
        this.typeAttributes.push(...data.results);
        for (let i = 0; i < event.length; i++) {
          let element = event[i];
          if(element.value == 'Glass' && this.optionAttsGlass == '') {
            this.optionAttsGlass = this.typeAttributes.filter(el => el.txtItemType == element.value)
          } else if(element.value == 'Case' && this.optionAttsCase == '') {
            this.optionAttsCase = this.typeAttributes.filter(el => el.txtItemType == element.value)
          } else if(element.value == 'Frame Standard' && this.optionAttsFrame == '') {
            this.optionAttsFrameStandard = this.typeAttributes.filter(el => el.txtItemType == element.value)
          } else if(element.value == 'Frame Eco' && this.optionAttsFrame == '') {
            this.optionAttsFrameEco = this.typeAttributes.filter(el => el.txtItemType == element.value)
          }
        }
      })
      .catch(err => console.log(err));
    },
    async updateTemplateGrid(event) {     
      let endpoint = `/api/inspection-template/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          txtInspectionTemplateName: event.node.data.txtInspectionTemplateName,
          txtInspectionTemplateType: event.node.data.txtInspectionTemplateType,
        })
        .catch(err => console.log(err));
      }
      catch (err) {
        console.log(err)
      }
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    addTemplate() {
      let selectedAttributes = this.selAtt1.concat(this.selAtt2, this.selAtt3, this.selAtt4)
      let endpoint = `/api/inspection-template/`;
      apiService(endpoint, "POST", { 
        txtInspectionTemplateName: this.TempName,
        txtInspectionTemplateType: this.Type.value,
        idInspectionTemplateAttribute: selectedAttributes, })
        .then(response => {
          if (response){
            this.getTemplates();
            this.emptyInput();
          }
        })
        .catch(err => console.log(err));
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Template? => " + params.node.data.txtInspectionTemplateName, {
          title: "Please Confirm",
          size: "m",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => { if (value) { this.deleteTemplate(params); } })
        .catch(err => console.log(err));
    },
    async deleteTemplate(params) {
      let endpoint = `/api/inspection-template/${params.node.data.id}/`;
      try {
        await apiService(endpoint, "DELETE");
        this.getTemplates();
      }
      catch (err) {
        console.log(err);
      }
    },
    emptyInput() {
      this.TempName = '';
      this.Type = null;
      this.IType = null;
      this.selAtt = null;
      this.optionAttsGlass = [];
      this.optionAttsCase = [];
      this.optionAttsFrame = [];
      this.show = false;
    },
    getTemplates() {
      if (!this.error) {
        this.templates = [];
        let endpoint = `/api/inspection-template-list/`;
        apiService(endpoint)
          .then(data => {
            this.templates.push(...data.results);
            this.rowData = this.templates
          })
          .catch(err => console.log(err));
      }
    },
    getAttributes() {
      if (!this.error) {
        this.attributes = [];
        let endpoint = `/api/inspection-template-attribute/`;
        apiService(endpoint)
          .then(data => { this.attributes.push(...data.results); })
          .catch(err => console.log(err));
      }
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    getContextMenu(params) {
      var result = [
        {
          name: 'Delete Template',
          action: () =>  this.openModal(params)
        },
        'export'
      ];
      return result;
    },
  },
  created() {
    this.getTemplates();
    this.getAttributes();
    this.getDateToday();
  },
};
