import { apiService } from "@/common/api.service.js";
import { AgGridVue } from '@ag-grid-community/vue';
import { AllModules } from '@ag-grid-enterprise/all-modules';

export default {
  metaInfo: {    
    title: "Inspection Attributes"
  },
  components: {
    AgGridVue
  },
  data() {
    return {
      // AG-GRID VARIABLES
      gridOptions: null,
      gridApi: null,
      columnApi: null,
      columnDefs: null,
      rowData: null,
      rowSelection: null,
      defaultColDef: null,
      frameworkComponents: null,
      domLayout: null,
      modules: AllModules,

      // VUE VARIABLES
      attributes: [],
      show: false,
      IType: '',
      KeyAtt: '',
      NameAtt: '',
      ValueDef: '',
      ValueType: '',
      Parameter: '',
      ValueMin: '',
      ValueMax: '',
      ValueTol: '',
      date: null,
      error: false,
      optionsTypes: [
        { value: 'INTEGER', text: 'Number' },
        { value: 'FLOAT', text: 'Decimal' },
        { value: 'STRING', text: 'Text' },
        { value: 'BOOLEAN', text: 'True/False' },
      ],
      optionsItemTypes: [
        { value: 'Glass', text: 'Glass' },
        { value: 'Case', text: 'Case' },
        { value: 'Frame', text: 'Frame' },
      ],
      optionsUnits: [
        { value: 'µm', text: 'Mymetre' },
        { value: 'mm', text: 'Millimetre' },
        { value: 'cm', text: 'Centimetre' },
        { value: 'mg', text: 'Milligram' },
        { value: 'g', text: 'Gram' },
        { value: 'ml', text: 'Millilitre' },
        { value: 'in', text: 'Inch' },
        { value: 'oz', text: 'Ounce' },
      ],
    };
  },
  beforeMount() {
    this.gridOptions = {};
    this.defaultColDef = { editable: true, resizable: true, sortable: true, filter: true, floatingFilter: true, };
    this.rowSelection = 'single';
    this.domLayout = 'autoHeight';
    this.paginationPageSize = 10;
    this.columnDefs = [
      {headerName: "Item Type", field: "txtItemType", filter: 'agTextColumnFilter', 
        cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: [{ code: 'INTEGER', name: 'Number' }, { code: 'FLOAT', name: 'Decimal' },]}},
      {headerName: "Key Attribute", field: "txtKeyAttribute", filter: 'agTextColumnFilter',},
      {headerName: "Name Attribute", field: "txtNameAttribute", filter: 'agTextColumnFilter', },
      {headerName: "Value Default", field: "txtValueDefault", filter: 'agTextColumnFilter', },
      {headerName: "Value Type", field: "txtValueType", filter: 'agTextColumnFilter', 
        cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['Glass', 'Case', 'Frame']}},
      {headerName: "Parameter", field: "txtParameter", filter: 'agTextColumnFilter', 
        cellEditor: 'agRichSelectCellEditor', cellEditorParams: { cellHeight: 50, values: ['µm', 'mm', 'cm', 'mg', 'g', 'ml', 'in', 'oz']}},
      {headerName: "Value Min", field: "txtValueMin", filter: 'agTextColumnFilter', },
      {headerName: "Value Max", field: "txtValueMax", filter: 'agTextColumnFilter', },
      {headerName: "Value Tolerance", field: "txtValueTolerance", filter: 'agTextColumnFilter', },
    ]
  },
  mounted() {
    if (JSON.parse(localStorage.getItem('inspectionAttributeTemp_columnStats')) != null) {
       this.gridOptions.columnApi.setColumnState(JSON.parse(localStorage.getItem('inspectionAttributeTemp_columnStats')));
    } else {
      this.gridApi = this.gridOptions.api;
      this.gridColumnApi = this.gridOptions.columnApi;
    }
  },
  computed: {
    disabled: function () {
      return !this.IType ? true : !this.KeyAtt ? true : !this.NameAtt ? true : !this.ValueDef ? true : !this.ValueType ? true : false; 
    },
  },
  methods: {
    saveState() {
      localStorage.setItem('inspectionAttributeTemp_columnStats', JSON.stringify(this.gridOptions.columnApi.getColumnState()));
    },
    restoreState() {
      localStorage.setItem('inspectionAttributeTemp_columnStats', null);
    },
    onPageSizeChanged(newPageSize) {
      var value = document.getElementById('page-size').value;
      this.gridApi.paginationSetPageSize(Number(value));
    },
    onRowSelected(event) {
      this.show = this.show ? false : true
    },
    autoSizeAll(skipHeader) {
      var allColumnIds = [];
      this.gridColumnApi.getAllColumns().forEach(function(column) {
        allColumnIds.push(column.colId);
      });
      this.gridColumnApi.autoSizeColumns(allColumnIds, skipHeader);
    },
    addAtt() {
      if (this.IType) {
        let endpoint = `/api/inspection-template-attribute/`;
        apiService(endpoint, "POST", { 
          txtItemType: this.IType,
          txtKeyAttribute: this.KeyAtt,
          txtNameAttribute: this.NameAtt,
          txtValueDefault: this.ValueDef,
          txtValueType: this.ValueType,
          txtParameter: this.Parameter,
          txtValueMin: this.ValueMin,
          txtValueMax: this.ValueMax,
          txtValueTolerance: this.ValueTol,
          })
          .then(response => {
            if (response != 'ERROR'){
              this.getAtt();
              this.emptyInput();
            }
          })
          .catch(err => console.log(err));
      }
    },
    async updateAttributeGrid(event) {     
      let endpoint = `/api/inspection-template-attribute/${event.node.data.id}/`;
      try {
        await apiService(endpoint, "PATCH", { 
          txtItemType: event.node.data.txtItemType,
          txtKeyAttribute: event.node.data.txtKeyAttribute,
          txtNameAttribute: event.node.data.txtNameAttribute,
          txtValueDefault: event.node.data.txtValueDefault,
          txtValueType: event.node.data.txtValueType,
          txtParameter: event.node.data.txtParameter,
          txtValueMin: event.node.data.txtValueMin,
          txtValueMax: event.node.data.txtValueMax,
          txtValueTolerance: event.node.data.txtValueTolerance,
        })
        .catch(err => console.log(err));
      }
      catch (err) {
        console.log(err)
      }
    },
    openModal(params) {
      this.$bvModal
        .msgBoxConfirm("Are you sure you want to delete the selected Attribute? => " + params.node.data.txtNameAttribute, {
          title: "Please Confirm",
          size: "m",
          buttonSize: "sm",
          okVariant: "danger",
          okTitle: "YES",
          cancelTitle: "NO",
          footerClass: "p-2",
          hideHeaderClose: false,
          centered: true
        })
        .then(value => { if (value) { this.deleteAttribute(params); } })
        .catch(err => console.log(err));
    },
    async deleteAttribute(params) {
      let endpoint = `/api/inspection-template-attribute/${params.node.data.id}/`;
      try {
        await apiService(endpoint, "DELETE")
        this.getAtt();
      }
      catch (err) {
        console.log(err)
      }
    },
    emptyInput() {
      this.IType = [];
      this.KeyAtt = '';
      this.NameAtt = '';
      this.ValueType = [];
      this.ValueDef = '';
      this.Parameter = [];
      this.ValueMin = '';
      this.ValueMax = '';
      this.ValueTol = '';
      this.show = false;
    },
    getAtt() {
      if (!this.error) {
        this.attributes = [];
        let endpoint = `/api/inspection-template-attribute/`;
        apiService(endpoint)
          .then(data => {
            this.attributes.push(...data.results);
            this.rowData = this.attributes
          })
          .catch(err => console.log(err));
      }
    },
    getDateToday() {
      var today = new Date();
      this.date = today.getDate()+' / '+(today.getMonth()+1)+' / '+today.getFullYear();
    },
    getContextMenu(params) {
      var result = [
        {
          name: 'Delete Attribute',
          action: () =>  this.openModal(params)
        },
        'export'
      ];
      return result;
    },
  },
  created() {
    this.getAtt();
    this.getDateToday();
  },
};
