    getSelectedOrder(event) {
      if(window.localStorage.getItem('selOrder') != null) {
        let el = parseInt(localStorage.getItem('selOrder'));
        event.api.forEachNode(rowNode => { rowNode.setSelected(rowNode.data.id === el) });
      }
